      @include single-transition($default-float, $topbar-transition-speed);

      ul {
        display: block;
        font-size: $rem-base;
        height: auto;
        margin: 0;
        padding: 0;
        width: 100%;
      }

      .divider,
      [role="separator"] {
        border-top: $topbar-divider-border-top;
        clear: both;
        height: 1px;
        width: 100%;
      }

      ul li {
      background: $topbar-dropdown-bg;

        > a {
          color: $topbar-link-color;
          display: block;
          font-family: $topbar-link-font-family;
          font-size: $topbar-link-font-size;
          font-weight: $topbar-link-weight;
          padding-#{$default-float}: $topbar-link-padding;
          padding: 12px 0 12px $topbar-link-padding;
          text-transform: $topbar-link-text-transform;
          width: 100%;

          &.button {
            font-size: $topbar-link-font-size;
            padding-#{$default-float}: $topbar-link-padding;
            padding-#{$opposite-direction}: $topbar-link-padding;
            @include button-style($bg:$primary-color);
          }

          &.button.secondary { @include button-style($bg:$secondary-color); }
          &.button.success { @include button-style($bg:$success-color); }
          &.button.alert { @include button-style($bg:$alert-color); }
          &.button.warning { @include button-style($bg:$warning-color); }
          &.button.info { @include button-style($bg:$info-color); }
        }

        > button {
          font-size: $topbar-link-font-size;
          padding-#{$default-float}: $topbar-link-padding;
          padding-#{$opposite-direction}: $topbar-link-padding;
          @include button-style($bg:$primary-color);

          &.secondary { @include button-style($bg:$secondary-color); }
          &.success { @include button-style($bg:$success-color); }
          &.alert { @include button-style($bg:$alert-color); }
          &.warning { @include button-style($bg:$warning-color); }
          &.info { @include button-style($bg:$info-color); }
        }

        // Apply the hover link color when it has that class
        &:hover:not(.has-form) > a {
          background-color: $topbar-link-bg-color-hover;
          color: $topbar-link-color-hover;

          @if ($topbar-link-bg-hover) {
            background: $topbar-link-bg-hover;
          }
        }

        // Apply the active link color when it has that class
        &.active > a {
          background: $topbar-link-bg-active;
          color: $topbar-link-color-active;

          &:hover {
            background: $topbar-link-bg-active-hover;
            color: $topbar-link-color-active-hover;
          }
        }
      }

      // Add some extra padding for list items contains buttons
      .has-form {
        padding: $topbar-link-padding;
      }

      // Styling for list items that have a dropdown within them.
      .has-dropdown {
        position: relative;

        > a {
          &:after {
            @if ($topbar-arrows) {
              @include css-triangle($topbar-dropdown-toggle-size, rgba($topbar-dropdown-toggle-color, $topbar-dropdown-toggle-alpha), $default-float);
            }

            margin-#{$opposite-direction}: $topbar-link-padding;
            margin-top: -($topbar-dropdown-toggle-size / 2) - 2;
            position: absolute;
            top: 50%;
            #{$opposite-direction}: 0;
          }
        }

        &.moved {
          position: static;

          > .dropdown {
            @include topbar-show-dropdown();
            width: 100%;
          }

          > a:after {
            display: none;
          }
        }
      }

      // Styling elements inside of dropdowns
      .dropdown {
        @include topbar-hide-dropdown();
        padding: 0;
        position: absolute;
        top: 0;
        z-index: 99;
        #{$default-float}: 100%;

        li {
          height: auto;
          width: 100%;

          a {
            font-weight: $topbar-dropdown-link-weight;
            padding: 8px $topbar-link-padding;
            &.parent-link {
              font-weight: $topbar-link-weight;
            }
          }

          &.title h5,
          &.parent-link {
            // Back Button
            margin-bottom: 0;
            margin-top: 0;
            font-size: $topbar-back-link-size;
            a {
              color: $topbar-link-color;
              // line-height: ($topbar-height / 2);
              display: block;
              &:hover { background:none; }
            }
          }

          &.has-form {
            padding: 8px $topbar-link-padding;
          }

          .button,
          button {
            top: auto;
          }
        }

        label {
          color: $topbar-dropdown-label-color;
          font-size: $topbar-dropdown-label-font-size;
          font-weight: $topbar-dropdown-label-font-weight;
          margin-bottom: 0;
          padding: 8px $topbar-link-padding 2px;
          text-transform: $topbar-dropdown-label-text-transform;
        }
      }
    }

    .js-generated { display: block; }


    // Top Bar styles intended for screen sizes above the breakpoint.
    @media #{$topbar-media-query} {
      .top-bar {
        @include clearfix;
        background: $topbar-bg;
        overflow: visible;

        .toggle-topbar { display: none; }

        .title-area { float: $default-float; }
        .name h1 a,
        .name h2 a,
        .name h3 a,
        .name h4 a,
        .name h5 a,
        .name h6 a { width: auto; }

        input,
        select,
        .button,
        button {
          font-size: rem-calc(14);
          height: $topbar-input-height;
          position: relative;
          top: (($topbar-height - $topbar-input-height) / 2);
        }

        .has-form > .button,
        .has-form > button {
          font-size: rem-calc(14);
          height: $topbar-input-height;
          position: relative; 
          top: (($topbar-height - $topbar-input-height) / 2);
        }

        &.expanded {
          background: $topbar-bg;
        }
      }

      .contain-to-grid .top-bar {
        margin: 0 auto;
        margin-bottom: $topbar-margin-bottom;
        max-width: $row-width;
      }

      .top-bar-section {
        @include single-transition(none,0,0);
        #{$default-float}: 0 !important;

        ul {
          display: inline;
          height: auto !important;
          width: auto;

          li {
            float: $default-float;
            .js-generated { display: none; }
          }
        }

        li {
          &.hover {
            > a:not(.button) {
              background-color: $topbar-link-bg-color-hover;
              @if ($topbar-link-bg-hover) {
                background: $topbar-link-bg-hover;
              }
              color: $topbar-link-color-hover;
            }
          }

          &:not(.has-form) {
            a:not(.button) {
              background: $topbar-link-bg;
              line-height: $topbar-height;
              padding: 0 $topbar-link-padding;
              &:hover {
                background-color: $topbar-link-bg-color-hover;
                @if ($topbar-link-bg-hover) {
                  background: $topbar-link-bg-hover;
                }
              }
            }
          }

          &.active:not(.has-form) {
            a:not(.button) {
              background: $topbar-link-bg-active;
              color: $topbar-link-color-active;
              line-height: $topbar-height;
              padding: 0 $topbar-link-padding;
              &:hover {
                background: $topbar-link-bg-active-hover;
                color: $topbar-link-color-active-hover;
              }
            }
          }
        }

        .has-dropdown {
          @if $topbar-arrows {
            > a {
              padding-#{$opposite-direction}: $topbar-link-padding + $topbar-link-dropdown-padding !important;
              &:after {
                @include css-triangle($topbar-dropdown-toggle-size, rgba($topbar-dropdown-toggle-color, $topbar-dropdown-toggle-alpha), top);
                margin-top: -($topbar-dropdown-toggle-size / 2);
                top: ($topbar-height / 2);
              }
            }
          }

          &.moved { position: relative;
            > .dropdown {
              @include topbar-hide-dropdown();
            }
          }

          &.hover, &.not-click:hover {
            > .dropdown {
              @include topbar-show-dropdown();
            }
          }

          > a:focus + .dropdown {
            @include topbar-show-dropdown();
          }

          .dropdown li.has-dropdown {
            > a {
              @if ($topbar-dropdown-arrows) {
                &:after {
                  border: none;
                  content: "\00bb";
                  top: rem-calc(3);

                  #{$opposite-direction}: 5px;
                }
              }
            }
          }
        }

        .dropdown {
          #{$default-float}: 0;
          background: transparent;
          min-width: 100%;
          top: auto;

          li {
            a {
              background: $topbar-dropdown-link-bg;
              color: $topbar-dropdown-link-color;
              line-height: $topbar-height;
              padding: 12px $topbar-link-padding;
              white-space: nowrap;
            }

            &:not(.has-form):not(.active) {
              > a:not(.button) {
                background: $topbar-dropdown-link-bg;
                color: $topbar-dropdown-link-color;
              }

              &:hover > a:not(.button) {
                background-color: $topbar-link-bg-color-hover;
                color: $topbar-dropdown-link-color-hover;
                @if ($topbar-dropdown-link-bg-hover) {
                  background: $topbar-dropdown-link-bg-hover;
                }
              }
            }

            label {
              background: $topbar-dropdown-label-bg;
              white-space: nowrap;
            }

            // Second Level Dropdowns
            .dropdown {
              #{$default-float}: 100%;
              top: 0;
            }
          }
        }

        > ul > .divider,
        > ul > [role="separator"] {
          border-#{$opposite-direction}: $topbar-divider-border-bottom;
          border-bottom: none;
          border-top: none;
          clear: none;
          height: $topbar-height;
          width: 0;
        }

        .has-form {
          background: $topbar-link-bg;
          height: $topbar-height;
          padding: 0 $topbar-link-padding;
        }

        // Position overrides for ul.right and ul.left
        .#{$opposite-direction} {
          li .dropdown {
            #{$default-float}: auto;
            #{$opposite-direction}: 0;

            li .dropdown { #{$opposite-direction}: 100%; }
          }
        }
        .#{$default-float} {
          li .dropdown {
            #{$opposite-direction}: auto;
            #{$default-float}: 0;

            li .dropdown { #{$default-float}: 100%; }
          }
        }
      }

      // Degrade gracefully when Javascript is disabled. Displays dropdown and changes
      // background & text color on hover.
      .no-js .top-bar-section {
        ul li {
          // Apply the hover link color when it has that class
          &:hover > a {
            background-color: $topbar-link-bg-color-hover;
            @if ($topbar-link-bg-hover) {
              background: $topbar-link-bg-hover;
            }
            color: $topbar-link-color-hover;
          }

          // Apply the active link color when it has that class
          &:active > a {
            background: $topbar-link-bg-active;
            color: $topbar-link-color-active;
          }
        }

        .has-dropdown {
          &:hover {
            > .dropdown {
              @include topbar-show-dropdown();
            }
          }

          > a:focus + .dropdown {
            @include topbar-show-dropdown();
          }
        }
      }
    }
  }
}

