layouts/header/header.scss:
// =============================================================================
// HEADER (CSS)
//
// 1. Setting font size of header-logo to 0, since wrapper element may vary, and
//    inline-block whitespace messes with header height. Font-size is reset to
//    appropriate size on header-logo-text.
// 2. Needs left/right margin so it doesn't cover left/right nav bar toggles
// 3. Offset the letter-spacing on the last character by adding a right negative
//    margin. This centers the text logo properly.
// 4. When the word "cart" is added to the mobile header's cart link, add more
//    margin to compensate.
//
// -----------------------------------------------------------------------------

$font-family: 'Lato', sans-serif;
$red: #d51a3a;
$normal: 400;
$bold: 700;

body {
    padding-top: 0px;
}
.banners[data-banner-location="top"] {
    background: #000 !important;
    font-family: "Lato", sans-serif;
    font-weight: 700;
    font-size: 13px;
    padding: 12px 20px;
    line-height: 16px;
    width: 100%;
}
.menu-txt{
    font-size:13px;
    font-family:$font-family;
    font-weight:$bold;
    position: absolute;
    top: 10px;
    left: 45px;
    color: #000;
    margin-left:2px;
}
.click-tog-search{
    font-family:$font-family;
    color:#000;
    font-weight:$bold;
    cursor:pointer;
    font-size: 13px;
    width: 82px;
    margin-left: calc(50% - 41px);
    margin-top:2px;
}
.black-outline{
    border:1px solid #000 !important;
}
.click-tog-search:before{
    content: "";
    background: url("../../assets/img/search-2x-black.png");
    display: inline-block;
    background-position: center center;
    background-size: 22px 22px;
    height: 22px;
    width: 22px;
    position:relative;
    top: 6px;
    margin-right:5px;
}
.click-tog-search.open-search::before {
    background: url("../../assets/img/black-x.jpg");
    background-size: 18px 18px;
    background-repeat: no-repeat;
    top: 8px;
    left: 2px;
}
.navPages-quickSearch.mob-tog-search {
    display:none;
    width: 100%;
    position: absolute;
    top: 115px;
    padding: 0 20px;
    background: #333;
    border: none;
    height:80px;
}
#search_query {
    background: #333;
    border: none;
    border-bottom: 1px solid #fff;
    border-radius: 0px;
    color: #fff !important;
}
.body{
    margin-top:15px !important;
}
.has-activeNavPages .body{
    margin-top:0px !important;
}

.header {
    background-color: $header-bg;
    border-bottom: container("border");
    max-width: 1280px;
    margin: 0 auto;
    
    top: 0px;
    width: 100%;
    z-index: zIndex("low");
    height:157px;
    // position:absolute;
    position:relative;

    @include breakpoint("medium") {
        border-bottom: container("border");
        overflow: visible;
        position: relative;
    }

    &.is-open {
        height: 100%;

        @include breakpoint("medium") {
            height: auto;
        }
    }
}



.header-logo {
    font-size: 0;
    // padding: 15px 0px 5px;
    text-align: center;
    background: #fff;
    margin: 0px;
    display:table;
    width:100%;
    height:60px;

    @include breakpoint("small") { // 4
        // margin-left: remCalc($header-toggle-width * 1.5);
        // margin-right: remCalc($header-toggle-width * 1.5);
        margin-left: 0;
        margin-right: 0;
    }

    @include breakpoint("medium") {
        margin: (spacing("double") + spacing("base")) auto (spacing("double") + spacing("single"));
        max-width: grid-calc(8, $total-columns);
        padding: 0;

        .header.fixed & {
            background-color: color("greys", "lightest");
            margin: 0;
            padding: spacing("quarter") 0;
            position: absolute;
            top: 0;
            width: 100%;
        }
    }

    > a {
        @include clearfix;
        color: $storeName-color;
        display: inline-block;
        padding: $header-logo-paddingVertical 0;
        position: relative;
        text-decoration: none;
        width: 100%;
        z-index: zIndex("low");

        display: table-cell;
        text-align: center;
        vertical-align: middle;

        // scss-lint:disable NestingDepth
        @include breakpoint("medium") {
            background: none;
            border-bottom: 0;
            display: inline;
            padding: 0;
        }

        &:hover {
            color: color("link", "hover");
        }

        &:active {
            color: color("link", "active");
        }
    }
}

.header-logo-text {
    display: block;
    font-family: $fontFamily-hero;
    font-size: fontSize("small");   // 1
    font-weight: stencilFontWeight("logo-font");
    letter-spacing: remCalc(5px);
    margin-left: auto;
    margin-right: auto;
    overflow: hidden;
    padding: remCalc(3px) 0;
    text-overflow: ellipsis;
    text-transform: uppercase;
    white-space: nowrap;

    @include breakpoint("small") {
        font-size: fontSize("large");
        letter-spacing: remCalc(9px);
        padding-bottom: 0;
        padding-top: 0;
    }

    @include breakpoint("medium") {
        display: inline;
        font-size: fontSize("largest");
        margin-left: 0;
        margin-right: -(remCalc(9px)); // 3
        max-width: none;
        overflow: auto;
        white-space: normal;

        .header.fixed & {
            font-size: fontSize("larger");
        }
    }
}

.header-logo-image {
    max-height: remCalc($header-height) - $header-logo-marginVertical * 2;

    @include breakpoint("medium") {
        max-height: none;
    }
}


//
// Mobile Menu Toggle
//
// 1. Animate lines from 'x' to hamburger
// -----------------------------------------------------------------------------

.center-wrap {
