}
#search_query_adv:-moz-placeholder { /* Firefox 18- */
    color: #fff;
}
#search_query_adv::-moz-placeholder {  /* Firefox 19+ */
   color: #fff;
}
#search_query_adv:-ms-input-placeholder {  
