   opacity:1;
}
#nl_email:-moz-placeholder { /* Firefox 18- */
   color: #888;
   opacity:1;
}
#nl_email::-moz-placeholder {  /* Firefox 19+ */
   color: #888; 
   opacity:1;
}
#nl_email:-ms-input-placeholder {  
