        text-align: center;
        width: 100%;
    }
    .footer-info-col.footer-subscription {
        max-width: 480px;
        margin: 0 auto;
        display: block;
    }
    
}
@media only screen and (min-width: 768px) {
    
    .social-icons h4 {
        display: block;
        margin-top: 0px;
        color:#000;
    }
    .social-icons {
        width: calc(33.33% - 0px);
        padding-left:20px;
        padding-right:20px;
        float: left;
        text-align: center;
        border-right: 1px solid #000;
    }
    .footer-info-col{
        width: calc(33.33% - 0px) !important;
        float: right;
        padding-left: 20px;
        padding-right: 20px;
    }
    .footer{
        width:100%;
    }
    .footer-info-col.footer-subscription {
        float: right;
        width:  calc(33.33% - 0px);
        border-left: 1px solid #000;
        padding-left:20px;
        padding-right:20px;
        padding-top: 25px;
        padding-bottom: 22px;
        height: 129px;
    }
    .contact-us{
        border-top:none;
    }
    .contact-us ul{
        display:none;
    }
    .social-icons ul li{
        margin:0px;
    }
    .footer{
        padding-top: 0px;
    }
    .copyright ul{
        display:block;
        margin: 10px auto;
    }
    .copyright ul li{
        display:inline-block;
    }
    .copyright ul li a{
        text-decoration: none;
        padding:10px;
        color:#000;
        font-size:14px;
        font-weight:$bold;
    }
    .mobile-break{
        display:inline;
        margin: 0px 3px;
    }
    .contact-us h4{
        margin-top: 0px;
    }
    .social-icons{
        padding-top: 25px;
        padding-bottom: 22px;
        height:129px;
    }
    .footer-inside-container {
        margin-top: -1px;
    }
    .contact-us{
        padding-top: 25px;
        padding-bottom: 13px;
    }
    .mobile-break::before {
        content: "|";
        margin: 0px 2px;
    }
    .navPages-action.has-subMenu {
        margin-left: 0px;
    }






}

@media only screen and (min-width: 992px) {

    .copyright > ul {
        float: left;
    }
    .copyright ul li a{
        font-size:14px;
    }
    .copyright > p {
        float: right;
        padding: 13px 0px 7px;
    }
    .social-icons ul li {
        margin: 0px 2% !important;
    }
    .footer {
        padding-top: 0px;
        max-width: 1280px;
        margin: 0 auto;
    }
    .slick-next, .slick-prev, .slick-next:hover, .slick-prev:hover {
        background: rgba(0,0,0,.1);
    }


    
