layouts/blog/blog.scss:
// =============================================================================
// BLOG
// =============================================================================


// Blog post
// -----------------------------------------------------------------------------

.blog {
    margin-bottom: spacing("single");

    @include breakpoint("medium") {
        margin: auto;
        width: 100%;
    }

    + .blog {
        border-top: container("border");
        margin-top: spacing("double");
        padding-top: spacing("double");
    }

    .tags {
        margin-top: spacing("double");
        text-align: center;
    }

    .socialLinks {
        text-align: center;
    }

    // scss-lint:disable SelectorFormat
    .addthis_toolbox {
        margin-top: spacing("double");
    }
    // scss-lint:enable SelectorFormat
}

.blog-title {
    font-size: fontSize("large");
    margin: 0 spacing("single") spacing("quarter");

    a {
        text-decoration: none;
    }
}

.blog-date,
.blog-author {
    color: stencilColor("color-textSecondary");
    text-align: center;
}

.blog-author {
    margin-bottom: spacing("single") + spacing("base");
}

.blog-post {
    font-size: fontSize("smaller");
    text-align: left;
}

.blog-thumbnail {
    margin: 0 0 (spacing("base") * 2);
}

.blog-post-figure {
    display: block;
    margin: auto;
    text-align: center;

    @include breakpoint("medium") {
        width: grid-calc(8, $total-columns);
    }
}

.blog-post-body {
    display: block;
    margin: 0 auto;
    text-align: center;
    width: 100%;

    @include breakpoint("small") {
        width: grid-calc(10, $total-columns);
    }

    @include breakpoint("medium") {
        width: grid-calc(8, $total-columns);
    }

    @include breakpoint("large") {
        width: grid-calc(6, $total-columns);
    }
}

layouts/body/body.scss:
// =============================================================================
// BODY (CSS)
//
// 1. Header is fixed on small screens, use the content body to create the whitespace
// between it and the header on all situations
//
// =============================================================================


/*# REBILLIA START #*/ #CheckoutStepPaymentDetails .ExpressCheckoutContent { opacity: 0; visibility: hidden; } .rebillia-manage-account { opacity: 0; visibility: hidden; display: none; } /*# REBILLIA END #*/

h1, h2, h3, h4, h5, h6{
    font-family:$font-family;
    color:#000;
}

@keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Firefox < 16 */
@-moz-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Safari, Chrome and Opera > 12.1 */
@-webkit-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Internet Explorer */
@-ms-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Opera < 12.1 */
@-o-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

.body {
    margin-bottom: spacing("double") * 2;
    margin-top: spacing("single"); // 1

    @include breakpoint("medium") {
        margin-top: spacing("single") + spacing("base"); // 1
    }
}

.page-content.page-content--centered.contact-form-success > p{
    text-align: center;
}

.cf:after{
    content: "";
    display: table !important;
    clear: both !important;
}


.page {
    @include grid-row(
        $behavior: nest
    );
}

.page-content,
.page-content--full {
    @include grid-column(12);
}

.page-content--centered {

    @include breakpoint("medium") {
        @include grid-column(
            $columns: 8,
            $center: true
        );
    }
}

.page-content--textCenter {
    text-align: center;
}

.page-sidebar {
    @include grid-column(12);
    position: relative;

    @include breakpoint("medium") {
        @include grid-column(3);

        + .page-content {
            @include grid-column(9);
        }
    }
}

.page-sidebar, .page-content, .page-content--full {
    padding-left: 0;
    padding-right: 0;
}
.slick-next, .slick-prev{
    width:40px;
    height:40px;
    border-radius:50%;
    background:rgba(255,255,255,0.7);
}
.button{
    border-radius:0px !important;
}
.form-input{
    border-radius:0px !important;
}

@media only screen and (min-width: 0px) {
    .desktop-slider{
        display:none;
    }
    .mobile-slider{
        display:block;
        width:100%;
        margin: 0 auto;
    }
    .mobile-slider .slick-slide{
        // padding-bottom:16.4%;
    }
    .mobile-slider .slick-slide > a {
        padding-bottom: 83%;
        display: block;
    }
    .slick-slide{
        background:no-repeat;
        cursor:pointer;
    }
    .mobile-slider .slick-prev, .mobile-slider .slick-next{
        display:none !important;
    }
    .desktop-slider .slick-prev, .desktop-slider .slick-next{
        
    }
    .featured-brands h4 {
        font-family: $font-family;
        text-align: center;
        font-weight:$bold;
        font-size:20px;
        margin-top: 0px;
        margin-bottom: 0px;
    }
    .featured-brands {
        margin:20px 20px 40px;
    }
    .featured-brands ul{
        margin:0px 0px 20px 0px;
        list-style:none;
        text-align:center;
    }
    .featured-brands ul > li{
        display:inline-block;
        width: 50%;
        padding:11px;
        border-bottom:1px solid #ccc;
    }
    .featured-brands ul > li:nth-child(n+7){
        border-bottom:none;
    }
    .featured-brands ul > li:nth-child(odd){
        border-right:1px solid #ccc;
    }
    .featured-brands ul > li:hover > a > img{
        opacity:1;
    }
    .banner-one, .banner-two, .banner-three{
        position:relative;
    }
    .banner-one > a > img, .banner-two > a > img, .banner-three > a > img{
        max-width: 100%;
        margin: 0 auto;
        display: block;
    }
    .banner-one .text, .banner-two .text, .banner-three .text{
        position: absolute;
        z-index: 99;
        color: #fff;
        top: 20px;
        width: calc(100% - 20px);
        left: 10px;
        text-align: center;
        font-family:$font-family;
        height: 120px;
        top: calc(50% - 60px);
    }
    .banner-one .text h4, .banner-two .text h4, .banner-three .text h4{
        color:#fff;
        font-weight:$bold;
        text-transform: uppercase;
        font-size:30px;
        margin-bottom: 0px;
        margin-top:0px;
    }
    .banner-one .text span, .banner-two .text span, .banner-three .text span{
        border: 1px solid #fff;
        padding: 10px 15px 8px;
