// Text Kerning
// -----------------------------------------------------------------------------

@mixin u-textKern($important: null) {

    // -------------------------------------------------------------------------
    //
    // 1. Chrome (not Windows), Firefox, Safari 6+, iOS, Android
    // 2. Chrome (not Windows), Firefox, IE 10+
    // 3. Safari 7 and future browsers
    //
    // -------------------------------------------------------------------------

