    .form-input-indicator {

        @include square($form-inputIndicator-size);
        float: left;
        margin: $form-inputIndicator-margin; // 1
        position: relative;
        top: $form-inputIndicator-top; // 1

        .form-prefixPostfix & {
            top: 0; //specific override due to flexbox middle alignment.
        }

        > svg {
            fill: $form-success-color;
        }

    }

    .form-field-errors {
        @if not $form-field-errors-bullets {
            @include u-listBullets("none");
            margin-left: 0;
        }
        clear: both;
        margin-bottom: $form-field-errors-marginBottom;
        margin-top: $form-field-errors-marginTop;
    }

    .form-inlineMessage {
        display: inline-block;
        font-weight: $form-inlineMessage-fontWeight;
        line-height: $form-inlineMessage-lineHeight;
        margin: $form-inlineMessage-margin;
        width: 100%;
    }


    //
    // FORM FIELD VALIDATION MODIFIERS
    // -----------------------------------------------------------------------------

    .form-field--success,
    .form-field--error,
    .form-field--warning {

        .form-input {
            float: left; //needed to place icons in the inputs
        }

    }

    //
    // VALID STYLES
    // -----------------------------------------------------------------------------

    .form-field--success {

        .form-input,
        .form-select,
        .form-checkbox + .form-label::before,
        .form-radio + .form-label::before,
        .form-prefixPostfix-label {
            border-color: $form-success-color;
        }

        .form-field-error,
        .form-inlineMessage {
            color: $form-success-color;
        }

        // Depth need to style svg with validation state
        // scss-lint:disable SelectorDepth
        .form-input-indicator > svg {
            fill: $form-success-color;
        }
        // scss-lint:enable SelectorDepth

    }

    //
    // INVALID STYLES
    // -----------------------------------------------------------------------------

    .form-field--error {

        .form-input,
        .form-select,
        .form-checkbox + .form-label::before,
        .form-radio + .form-label::before,
        .form-prefixPostfix-label {
            border-color: $form-error-color;
        }

        .form-field-error,
        .form-inlineMessage {
            color: $form-error-color;
        }

        // Depth need to style svg with validation state
        // scss-lint:disable SelectorDepth
        .form-input-indicator > svg {
            fill: $form-error-color;
        }
        // scss-lint:enable SelectorDepth


    }

    //
    // WARNING STYLES
    // -----------------------------------------------------------------------------

    .form-field--warning {

        .form-input,
        .form-select,
        .form-checkbox + .form-label::before,
        .form-radio + .form-label::before,
        .form-prefixPostfix-label {
            border-color: $form-warning-color;
        }

        .form-field-error,
        .form-inlineMessage {
            color: $form-warning-color;
        }

        // Depth need to style svg with validation state
        // scss-lint:disable SelectorDepth
        .form-input-indicator > svg {
            fill: $form-warning-color;
        }
        // scss-lint:enable SelectorDepth

    }
}

// =============================================================================
// BC FORM CREDIT CARD FIELDS (CSS)
// =============================================================================


// Bigcommerce Component
// -----------------------------------------------------------------------------
//
// The Bigcommerce Coding Standards, coupled with Foundation Variables.
//
// 1. If set to false, there is no outputted CSS for this component.
// 2. This matches the variable of Foundation forms, as they come together
//
// -----------------------------------------------------------------------------

@if $exportCSS--bcForm-ccFields { // 1, 2
    // Credit Card Form Fields
    // -----------------------------------------------------------------------------
    //
    // Purpose: Predefined pattern for credit card payment forms
    //
    // 1. Override the default max-width of form-field
    //
    // 2. Override the max-width again, because we really only want expiry and cvv
    // to be very short fields based on input, no matter what the layout, except small
    // screens which is 50/50
    //
    // 3. Override the icon's height because the cvc icon is not a square. Otherwise,
    // it would appear there is a top margin
    //
    // .form-ccFields
    //     > .form-field--ccNumber
    //     > .form-field--ccExpiry
    //     > .form-field--ccName
    //     > .form-field--ccCvv
    //
    // -----------------------------------------------------------------------------

    .form-ccFields {
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
        margin: $form-ccFields-margin;
        padding: $form-ccFields-padding;

        > .form-field {
            max-width: none; // 1
            padding: $form-ccFields-field-padding;
        }
    }

    .form-field--ccNumber {
        order: 1;
    }

    .form-field--ccExpiry {
        order: 2;
    }

    .form-field--ccName {
