    .card-title {
        margin-bottom: $card-title-marginBottom;
    }

    .card-subTitle {
        margin-bottom: $card-subTitle-marginBottom;

        .card-title + & {
            margin-top: $card-subTitle-adjacentCardTitleMarginTop;
        }
    }

    .card-text {
        font-size: $card-text-fontSize;
