        border: $card-footer-border;
        border-width: $card-footer-borderWidth;
        margin: $card-footer-margin;
        padding: $card-footer-padding;
    }

    .card-button {
        margin-bottom: 0;

        &:not(.button--small) {
            width: $card-button-width;
        }

    }
}

